CREATE TABLE WorkloadManagement(
   employee_id       VARCHAR(8) NOT NULL,
   assignment_date   DATE       NOT NULL,
   starting_weekday  NUMBER(1)  NOT NULL,
   ending_weekday    NUMBER(1)  NOT NULL,
   starting_time     date  NOT NULL,
   ending_time       date  NOT NULL,
   CONSTRAINT fk_workload_management FOREIGN KEY(employee_id) REFERENCES Employee(employee_id),
   primary key(employee_id, assignment_date)
);





select count(*) as TotalWorkloadManagementRecords from WorkloadManagement;